/*
    Convert bitmaps from horizontal mode to page mode
*/
void BitmapHorizontalToPageMode(uint8_t *horizontalModeBitmap, uint8_t *pageModeBitmap, uint8_t width, uint8_t height)
{
    //height & width must be multiple of 8
    if((width % 8) != 0 || (height % 8) != 0)
    {
        ESP_LOGE(TAG, "Width or height not multiple of 8");
        return;
    }

    uint8_t rows = height / 8;
    uint8_t cols = width / 8;

    for(int z=0; z < rows; z++)
    {
        for(int k=0; k < cols; k++)
        {
            for(int j=0; j < 8; j++)
            {
                pageModeBitmap[j + k * 8 + z * width] = 0;
                for(int i=0; i < 8; i++)
                {
                    pageModeBitmap[j + k * 8 + z * width] |= ((horizontalModeBitmap[k + i * cols + z * width] & (0x80 >> j)) << j >> i);
                }
            }
        }
    }
    return;
}

